/* Inserir Operacoes Colheita Lameiro Da Ponte  */

/*test operacoes colheita in lameiro da ponte*/
DECLARE
    LISTA         SYS_REFCURSOR;
    NOME          VARCHAR2(20);
    PLANTA_ID     NUMBER;
    QUANTIDADE_KG FLOAT;
    V_PARCELA     VARCHAR2(20) := 'Lameiro Da Ponte';
    DATA_INICIAL  DATE := TO_DATE('2015-08-01', 'YYYY-MM-DD');
    DATA_FINAL    DATE := TO_DATE('2023-11-30', 'YYYY-MM-DD');
BEGIN
    LISTA := LISTA_PRODUTOS_COLHIDOS (V_PARCELA, DATA_INICIAL, DATA_FINAL);
    dbms_output.put_line ('PLANTA_ID | NOME | QUANTIDADE_KG');
    DBMS_OUTPUT.PUT_LINE ('----------------------------------------------------------------------');
    LOOP
        FETCH LISTA INTO PLANTA_ID, NOME, QUANTIDADE_KG;
        EXIT WHEN LISTA%NOTFOUND;
        DBMS_OUTPUT.PUT_LINE (PLANTA_ID
                              || ' | '
                              || NOME
                              || ' | '
                              || QUANTIDADE_KG || ' kg');
    END LOOP;
    CLOSE LISTA;
END;
/



